/* ==UserStyle==
@name           Thunder-W
@namespace      https://github.com/Catgrills/Thunder-W
@version        1.0.0
@description    An userstyle for Tumblr called Thunder-W.
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F0F1F5 !important; 
		--card-bg: #FFF !important;
		--card-bg-2: #F2F2F2 !important;
		--border-color: #EBEBEB !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #201A23 !important; 
		--card-bg: #022B3A !important;
		--card-bg-2: #40434E !important;
		--border-color: #080705 !important;
		--primary-color: #FFF !important;
		--secondary-color: #BEBEBE !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("tumblr.com") {
/*[[theme]]*/
/* GENERAL */

    /** DESCRIPTION : change the elements of all page **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
    }

    body {
        overflow-x: hidden;
    }

	input::-moz-placeholder{
    	font-size: 14pt !important;
        font-stretch: condensed;
	}

    .popover_menu_item:hover,
    .blog-list-item:hover,
    .blog-sub-nav-details .blog-sub-nav-item:hover,
    .blog-sub-nav-details .blog-sub-nav-item.currentItem,
    .blog-sub-nav-details .blog-sub-nav-item.selected,
    .blog-sub-nav-details .blog-sub-nav-item:hover {
        background: transparent !important;
        box-shadow: none !important;
    }

    .postforms-open .l-container,
    .l-container.l-container--two-column-dashboard {

        /*** CONTAINER POSITION ***/
        position: absolute !important;
        width: 100% !important;
    }

    .post-forms-glass.active {

        /*** GLASS CONTAINER ***/
        display: none !important;
    }

    .ui_peepr_glass {
        background: rgba(52, 73, 94, .8) !important;
    }

    .results_end_body {

        /*** NO RESULT CONTAINER ***/
        color: #212121 !important;
        font-stretch: condensed !important;
    }

    .results_end_body .search_query_highlight {
        color: #83D6DE !important;
    }

    .no_posts_found {
        background: var(--body-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding: 10px !important;
        text-transform: lowercase !important;
    }

    .elevator-wrapper.visible {

        /*** TOP BUTTON ***/
        position: fixed !important;
        left: 40px !important;
        bottom: 45px !important;
    }

    #search_actions_search .elevator-wrapper.visible {
        bottom: 65px !important;
    }

    #discover_actions_discover .elevator-wrapper.visible {
        bottom: 45px !important;
        left: 28px !important;
    }

    .elevator-wrapper.visible .elevator {
        background-color: transparent !important;
        color: #009FB7 !important;
        font-size: 21pt !important;
    }

    .account-sessions.settings_group,
    .blogs_wrapper.indash_blogs,
    .nipple,
    .notification.single_notification.alt.takeover-container.big-margin,
    .popover_legal_link,
    #posts.posts > li.notification.single_notification,
    .post_brick .post_tags .post_tag::before,
	.post-brick-source-footer,
    .search_controls .right_controls,
    .tags-and-tumblelogs {

        /*** MISC ***/
        display: none !important;
    }

    .l-footer {
       visibility: hidden;   
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .l-header-container--refresh {
        padding: 10px !important;
    }

    #trigger-btn {
		background: no-repeat url("https://i.imgur.com/pGlESDe.png") center !important;
		background-color: transparent !important;
		background-size: 25px auto !important;
		border: none !important;
		color: transparent !important;
		height: 20px !important;
		width: 30px !important;
        position: fixed !important;
        top: 27px !important;
        left: 90px !important;
        filter: invert(1) !important;
    }

    .l-header-container .logo {
        margin-left: 20px !important;
        margin-right: 20px !important;
    }

    .l-header-container .logo .png-logo {
        background: no-repeat url("https://upload.wikimedia.org/wikipedia/commons/e/e0/Tumblr_icon.png") center !important;
        background-size: 40px auto !important;
        margin-top: 0px !important;
        margin-right: -10px !important;
    }

    .l-header-container .logo .png-logo:hover {
        opacity: .6 !important;
    }

    .preload-container {
        display: none !important;
    }

    .l-header-container #search_query {
        border-radius: 2px !important;
        font-family: "Roboto" !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        padding: 0px 11px 2px 11px !important;
    }

    .is-following.show-unfollow.search-follow-button .unfollow-text, 
    .worded-follow-button.is-following.show-unfollow .unfollow-text,
	.search-follow-button {
        display: none !important;
    }

    .l-header-container .ui_search.active .search_query,
    .l-header-container .ui_search.selected .search_query {
        background: rgba(0, 0, 0, .13) !important;
        color: inherit !important;
        transition: background 75ms, color 75ms;
    }

    .l-header-container .popover {
        background: var(--card-bg) !important;
        border-radius: 1px !important;
        border-top: 6px solid #1DABB8 !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 10px !important;
        margin-left: 3px !important;
    }

    .l-header-container .search_popover .search_results_section .search_subheading {
        display: none !important;
    }

    .l-header-container .popover .result_title {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        letter-spacing: .5px !important;
    }

    .l-header-container .result_sub_title {
        font-family: "Roboto" !important;
        font-size: 10pt !important;
    }

    .l-header-container .ui_search .search_form_field::before {
        display: none;
    }

    .tab_notice {
        background: transparent !important;
        border: none !important;
        line-height: 20px !important;
    }

    .tab_notice .tab_notice_value {
        color: #4CD4B0 !important;
        font-size: 12pt !important;
        margin-left: 5px !important;
    }

    .popover--inbox-popover .popover-inner {
        background: var(--card-bg) !important;
        border-top: 6px solid #97CE68 !important;
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding: 10px !important;
    }

    .popover--inbox-popover .popover-inner .messaging-inbox .inbox-header {
        border-bottom: none !important;
        padding-bottom: 20px !important;
    }

    .popover--inbox-popover .popover-inner .blog-selector-container .selected-blog .avatar {
        width: 50px !important;
        height: 50px !important;
    }

    .popover--inbox-popover .popover-inner .blog-selector-container .selected-blog .name {
        color: #65878F !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: .5px !important;
        text-transform: capitalize !important;
    }

    .popover--inbox-popover .popover-inner .compose-toggle.compose-start,
    .popover--inbox-popover .popover-inner .messaging-inbox .inbox-compose-toggle .compose-cancel {
        font-size: 12pt !important;
        font-weight: normal !important;
        text-transform: lowercase !important;
    }

    .popover--inbox-popover .popover-inner .messaging-inbox h2 {
        display: none !important;
    }

    .popover--inbox-popover .popover-inner .messaging-inbox .inbox-row h3 {
        color: #65878F !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: .5px !important;
    }

    .messaging-inbox .inbox-conversations .inbox-conversation .message,
    .flag--messaging-new-empty-inbox .messaging-inbox .inbox-row .info-container .tags {
        color: var(--secondary-color) !important;
    	font-size: 14pt !important;
        font-stretch: condensed !important;
        max-height: 100% !important;
    }

    .popover--inbox-popover .popover-inner .messaging-inbox .send-link {
        font-size: 12pt !important;
        font-weight: normal !important;
        padding-bottom: 5px !important;
        text-transform: lowercase !important;
    }

    .popover--inbox-popover .popover-inner .messaging-inbox .inbox-recipients .inbox-recipient {
        background: var(--card-bg) !important;
        border-top: 1px dotted var(--border-color) !important;
    }

    .messaging-conversations-container {
        z-index: 99 !important;
    }

    .messaging-conversation--open .conversation-header .controls .minimize,
    .messaging-conversation--open .conversation-header .controls .close,
    .messaging-conversation--open .conversation-header .controls .more-actions {
        height: 30px !important;
        width: 30px !important;
        font-size: 10pt !important;
    }

    .messaging-conversation--open .conversation-header .controls .close {
        margin-left: -5px !important;
        margin-right: 5px !important;
    }

    .popover--activity-popover .popover {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .05) !important;
    }

    .popover--activity-popover .popover_inner {
        background: var(--card-bg) !important;
        border-top: 6px solid #BF4A67 !important;
        box-shadow: none !important;
    }

    .popover--activity-popover .popover_gradient > .popover_inner:first-child,
    .popover--activity-popover .popover_gradient > .popover_inner:last-child {
        background: var(--card-bg) !important;
        border-top-right-radius: 1px !important;
        border-top-left-radius: 1px !important;
        border-bottom-right-radius: 1px !important;
        border-bottom-left-radius: 1px !important;
    }

    .popover--activity-popover .blog-selector-container .selected-blog .avatar {
        width: 50px !important;
        height: 50px !important;
    }

    .popover--activity-popover .blog-selector-container .selected-blog .name {
        color: #65878F !important;
        font-stretch: condensed !important;
        font-size: 14pt !important;
        font-weight: normal !important;
        letter-spacing: .5px !important;
        text-transform: capitalize !important;
    }

    .ui_notes .date_header, 
    .ui_notes .date_header.date_activity {
        background-color: var(--body-bg) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 15px !important;
    }    

    .part_relative_date {
        font-weight: normal !important;
        text-transform: capitalize !important;
    }

    .blog-activity .blog-activity-more {
        background: var(--card-bg) !important;
        border-top: 1px solid var(--border-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 8px 10px 10px 10px !important;
        text-transform: lowercase !important;
    }

    .ui_notes .activity-notification .activity-notification__avatar,
	.ui_notes .activity-notification .activity-notification__icon .note_follow {
        width: 20px;
        visibility: hidden !important;
    }

    .ui_notes .activity-notification .activity-notification__activity .activity-notification__activity_message * {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .popover--activity-popover .blog-activity,
    .blog-activity .blog-activity-content {
        border-top: 1px solid var(--border-color) !important;
    }

    .blog-activity .blog-activity--empty {
        background: #FFF !important;
        border-radius: 1px 1px 1px 1px !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .popover--account-popover .popover {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .popover--account-popover .popover_menu ul {
        padding: 10px !important;
    }

    .popover--account-popover .popover_gradient > .popover_inner:first-child {
        background: var(--card-bg) !important;
        border-top-right-radius: 1px !important;
        border-top-left-radius: 1px !important;
        border-top: 6px solid #F9AE74 !important;
        margin-left: -1px !important;
    }

    .popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog {
        background: var(--card-bg) !important;       
    }

    .popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog * {
        color: #8199A3 !important;
    }

    .blog-list-item .blog-list-item-info .blog-list-item-info-title {
    	font-size: 14pt !important;
        font-stretch: condensed;
        line-height: 18pt;
    }

    .popover--account-popover .popover_gradient > .popover_inner:last-child {
        border-bottom-right-radius: 1px !important;
        border-bottom-left-radius: 1px !important;
    }

    .popover--account-popover .popover .popover_header {
        background: transparent !important;
        border-bottom: 1px dotted var(--border-color) !important;
        color: #557780 !important;
        height: 35px !important;
        font-size: 12.5pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        letter-spacing: .3px !important;
    }

    .popover--account-popover .popover_menu_item_anchor {
        color: #8199A3 !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0.3px !important;
        text-transform: lowercase !important;
    }

    .popover--account-popover .popover_menu_item_anchor_icon {
        display: none !important;
    }

    .popover--account-popover .popover_menu > .popover_inner .popover_item_suffix a {
        font-size: 14pt !important;
        text-transform: lowercase !important;
    }

    .popover--account-popover .popover .popover_menu_item .popover_item_suffix {
        font-size: 12pt !important;
        font-stretch: condensed !important;
    }

    .popover--account-popover .popover .blog-list-item .blog-list-item-avatar {
        height: 50px !important;
        width: 50px !important;
    }

    .popover--account-popover .blog-list-item .blog-list-item-info .blog-list-item-info-name {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: .5px !important;
        padding-bottom: 5px !important;
        text-transform: capitalize !important;
    }

    .popover--account-popover .popover .popover_menu_item_blog_details {
        padding-left: 0px !important;
    }

    .blog-sub-nav-details .blog-sub-nav-item {
        color: #8199A3 !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0.3px !important;
        text-transform: lowercase !important;
    }

    .l-header-container .compose-button {
        display: none;
    }

    .discover-controls-wrapper {
        background: #1B3C4E !important;
        background-size: cover !important;
        border: none !important;
        padding: 16px 20px 24px 20px !important;
        width: 100% !important;
        position: fixed !important;
        right: 0px !important;
        left: 0px !important;
        bottom: -75px !important;
        z-index: 999 !important;
    }

    .content-control[href="/explore/asks"]::after {
        content: "" !important;
    }

    .discover-controls-wrapper .types-tabs a {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        margin-right: 0px !important;
    }

	.discover-controls-wrapper .types-tabs > .content-control.active {
    	font-weight: normal !important;
	}

    .discover-controls-wrapper .types-tabs > .content-control:nth-child(2) {
        margin-left: 3px !important;
    }

    /* DASHBOARD/TAGGED */

    /** DESCRIPTION : change the elements of the dashboard/tagged page **/
    .identity,
    .l-container.l-container--two-column-dashboard .l-content,
    .l-container.l-container--two-column-dashboard .left_column,
    .l-container.l-container--flex .l-content,
    .post_avatar.show_user_menu.post-avatar--sticky,
    #dashboard_useraction_following .l-content {

        /*** DASHBOARD/TAGGED BACKGROUND ***/
        background: var(--body-bg) !important;
    }

    #dashboard_index .l-container .l-content,
    #dashboard_posts_likes .l-container .l-content,
    #tagged_actions_display .l-container .l-content {

        /*** DASHBOARD/TAGGED/LIKES CONTENT ***/
        background: var(--body-bg) !important;
        width: 100% !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        top: -50px !important;
        margin: auto !important;
        text-align: center !important;
    }

    #dashboard_index #new_post_buttons,
    #dashboard_posts_likes #new_post_buttons,
    #tagged_actions_display #new_post_buttons {

        /*** DASHBOARD/TAGGED POSTS NAVBAR ***/
        width: 100% !important;
        position: fixed !important;
        bottom: -120px;
        left: 0px !important;
        opacity: 0.98 !important;
        z-index: 99 !important;
        transition: all.3s ease-in-out !important;
    }

    #dashboard_index #new_post_buttons #new_post,
    #tagged_actions_display #new_post_buttons #new_post,
    #dashboard_posts_likes #new_post_buttons #new_post {
        border-width: 1px 0px 0px 0px !important;
        width: 100% !important;
    }

    #dashboard_index #new_post_buttons #new_post .new_post_label,
    #tagged_actions_display #new_post_buttons #new_post .new_post_label,
    #dashboard_posts_likes #new_post_buttons #new_post .new_post_label {
        background: none !important;
        font-family: "Roboto " !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
        display: inline-block !important;
        float: none !important;
        margin-bottom: -93px !important;
    }

    .new_post_label i {
        font-size: 30pt !important;
        margin-left: 0px !important;
    }

    .new_post_label_text {
        display: none !important;    
    }

    #dashboard_index .post-form .post-container,
    #dashboard_posts_likes .post-form .post-container,
    #tagged_actions_display .post-form .post-container {
        background: var(--card-bg) !important;
        min-height: 400px !important;
        height: auto !important;
        width: 100% !important;
    }

    #dashboard_index .post-form--header .controls-container,
    #dashboard_posts_likes .post-form--header .controls-container,
    #tagged_actions_display .post-form--header .controls-container {
        background: #00587A;
        padding: 15px 22px 19px 22px !important;
    }

    #dashboard_index .tumblelog-select *,
    #dashboard_posts_likes .tumblelog-select *,
    #tagged_actions_display .tumblelog-select * {
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: .5px !important;
    }

    #dashboard_index .editor-placeholder,
    #dashboard_posts_likes .editor-placeholder,
    #tagged_actions_display .editor-placeholder {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        width: 100% !important;
    }

    #dashboard_index .editor-plaintext,
    #dashboard_index .editor-richtext,
    #dashboard_posts_likes .editor-plaintext,
    #dashboard_posts_likes .editor-richtext,
    #tagged_actions_display .editor-plaintext,
    #tagged_actions_display .editor-richtext {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
        padding-top: 4px !important;
        margin-top: 20px;
        width: 100% !important;
    }

    .tx-button,
	.post-form--controls .controls-container .post-form--save-button .split-button .button-area {
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column {

        /*** DASHBOARD/TAGGED LEFT COLUMN ***/
        width: 100% !important;
        margin-top: 200px !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column #posts.posts,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column #posts.posts,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column #posts.posts {

        /*** DASHBOARD/TAGGED LEFT COLUMN POSTS ***/
        margin: 0 6vw !important;
        display: flex !important;
        flex-flow: row wrap !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column  #posts.posts > .post_container,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column  #posts.posts > .post_container,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column  #posts.posts > .post_container {

        /*** DASHBOARD/TAGGED LEFT COLUMN POSTS CONTAINER ***/
        margin: 0 0px 20px 0 !important;
        display: inline-block !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column .post.post_full,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column .post.post_full,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column .post.post_full {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        margin-right: 20px !important;
        width: 27vw !important;
        overflow: hidden !important;
        transition: all .3s ease-in-out !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column .post.post_full:hover,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column .post.post_full:hover,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column .post.post_full:hover {
        border: 1px solid #009FB7 !important;
        transition: all .3s ease-in-out !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column .post_avatar,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .left_column .post_avatar,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .left_column .post_avatar {

        /*** DASHBOARD/TAGGED LEFT COLUMN POSTS AVATAR ***/
        display: none !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .left_column .post_header,
