    #dashboard_posts_likes .rapid-recs-container .rapid-recs .rapid-rec-title,
    #tagged_actions_display .rapid-recs-container .rapid-recs .rapid-rec-title {
        visibility: hidden;
    }

    #dashboard_index .rapid-recs-container .rapid-recs .close,
    #dashboard_posts_likes .rapid-recs-container .rapid-recs .close,
    #tagged_actions_display .rapid-recs-container .rapid-recs .close {
        visibility: visible !important;
    }

    #dashboard_index .rapid-recs-container .rapid-recs .icon_close,
    #dashboard_posts_likes .rapid-recs-container .rapid-recs .icon_close,
    #tagged_actions_display .rapid-recs-container .rapid-recs .icon_close {
        color: #000 !important;
    }

    #dashboard_index .l-container.l-container--two-column-dashboard .right_column,
    #dashboard_posts_likes .l-container.l-container--two-column-dashboard .right_column,
    #tagged_actions_display .l-container.l-container--two-column-dashboard .right_column {

        /*** DASHBOARD/TAGGED RIGHT COLUMN ***/
        display: none !important;
    }

    #dashboard_index .sidebar_nav.clearfix.visible,
    #dashboard_posts_likes .sidebar_nav.clearfix.visible,
    #tagged_actions_display .sidebar_nav.clearfix.visible {

        /*** FOOTER ***/
        display: none !important;
    }

    /* EXPLORE */

    /** DESCRIPTION : change the elements of the explore page **/
    .discover-content {

        /*** EXPLORE CONTAINER ***/
        margin-top: 150px !important;
    }

    .discover .discover-posts .post {

        /*** EXPLORE POSTS ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color)!important;
        border-radius: 1px !important;
        margin-bottom: 25px !important;
        overflow: hidden !important;
    }

    .discover .post_brick.reblog_header .post_header,
    .discover .post_media img {
        border-bottom: 1px solid var(--border-color) !important;
    }

    .discover .post-info-post-tumblelog a {
    	color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .post_brick.reblog_header .post_header .tumblelog_avatar {
        border-radius: 50% !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
    }

    .discover .follow_link.worded-follow-button .follow-text {
        background: #4EA699 !important;
        border-radius: 2px !important;
        padding: 2px 10px !important;
        margin-top: -1px !important;
        color: #FFF !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        letter-spacing: 0px !important;
        text-transform: uppercase !important;
    }

    .discover .post_brick.reblog_header .tumblelog_info.is_reblog .post-info-post-tumblelog,
	.discover .post_brick.reblog_header .tumblelog_info.is_reblog .post-info-post-tumblelog a {
        line-height: 27px !important;
        height: 50px !important;
    }

    .discover .post_brick.reblog_header .tumblelog_info.is_reblog .post-info-parent-tumblelog {
        display: none;    
    }

    .discover .post_brick .expand-post-content,
    .discover .post_brick.is_photo .post_media + .expand {

        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0px !important;
        text-transform: lowercase !important;
    }

    .discover .post_body,
    .discover .post_body * {
        color: var(--primary-color) !important;
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        line-height: 25px !important;
    }

    .discover .post_footer.clearfix {
        border-top: 1px dotted var(--border-color) !important;
        margin-top: 20px !important;
    }

    /* SEARCH */

    /** DESCRIPTION : change the elements of the search page **/
    #search_actions_search,
    #search_actions_search .search_results_bottom_wrapper.posts_wrapper {

        /*** SEARCH BACKGROUND ***/
        background: var(--body-bg) !important;
    }

    .search_controls .search_control_items {
        /*** SEARCH CONTROLS ***/
        margin-bottom: 20px !important;
    }

    .search_sub_header {
        visibility: hidden !important;   
    }

    .search_control_items .search_control_button,
	.search_control_items .search_control_link {
        color: #6E6E6E !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .search_control_items .launch_button .icon_arrow_carrot_down {
        color: #7E7E7E !important;
        margin-top: 3px !important;
    }

    #search_actions_search .post {

        /*** EXPLORE POSTS ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        margin-bottom: 25px !important;
        overflow: hidden !important;
    }

    #search_actions_search .post_brick.reblog_header .post_header,
    #search_actions_search .post_media img {
        border-bottom: 1px solid var(--border-color) !important;
    }

    #search_actions_search .post-info-post-tumblelog .post-info-tumblelog a {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    #search_actions_search .follow_link.worded-follow-button .follow-text {
        background: #4EA699 !important;
        border-radius: 2px !important;
        padding: 2px 10px !important;
        margin-top: -1px !important;
        color: #FFF !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        letter-spacing: 0px !important;
        text-transform: uppercase !important;
    }

    #search_actions_search .post_brick .expand-post-content,
    #search_actions_search .post_brick.is_photo .post_media + .expand {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0px !important;
        text-transform: lowercase !important;
    }

    #search_actions_search .post_body,
    #search_actions_search .post_body * {
        color: var(--primary-color) !important;
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        line-height: 25px !important;
    }

    #search_actions_search .post_footer.clearfix {
        border-top: 1px dotted var(--border-color) !important;
        margin-top: 20px !important;
    }

    /* SETTINGS */

    /** DESCRIPTION : change the elements of the setting page **/
    body[id*="settings_actions"] .l-container.l-container--two-column .l-content {

        /*** SETTINGS CONTAINERS ***/
        margin-top: 130px !important;
    }

    .l-container.l-container--two-column-dashboard .l-content,
    .l-container.l-container--two-column .l-content {

        /*** SETTINGS CONTAINERS BACKGROUND ***/
        background: var(--body-bg) !important;
    }

     .l-container.l-container--two-column .left_column {
		width: 600px !important;
    }

    .tumblr_settings .settings_heading {
        border-bottom: 1px solid var(--border-color) !important;
    }

    .tumblr_settings .settings_group + .settings_group,
    .tumblr_settings .settings_group + .settings_footer {
         border-top: 1px solid var(--border-color) !important;       
    }

    .l-container.l-container--two-column #left_column.white_pane {

        /*** SETTINGS CONTAINERS SETTINGS FORM ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        padding-bottom: 20px !important;
    }

    .indash_header_wrapper,
    .tumblr_settings--labs .settings-header-image {
        border-radius: 1px !important;
    }

    .settings_group_wrapper .settings_group {
        padding-top: 40px !important;
        padding-bottom: 30px !important;
    }

    .settings_subheading {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0px !important;
    }

    .group_content * {
        color: var(--primary-color) !important;
        font-size: 12pt !important;
    }

    .l-container.l-container--two-column #left_column {

        /*** SETTINGS CONTAINERS MAIN BOX ***/
        padding: 0px !important;
    }

    .l-container.l-container--two-column #left_column .settings_heading {
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        line-height: 25px !important;
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .settings_subheading {
        padding: 0px 20px !important;
    }

    .l-container.l-container--two-column #left_column .apps_heading {
        display: none !important;
    }

    .l-container.l-container--two-column #left_column .settings_group .group_label {
        padding: 0 3px !important;
    }

    .l-container.l-container--two-column #left_column .app.app--promo-row {
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .help_text {
        font-size: 11pt !important;
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .tumblr_settings .help_text a {
        font-size: 11pt !important;
    }

    .accordion_header.clearfix, 
    .accordion_header.clearfix * {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .tumblr_settings .setting.checkbox .binary_switch {
        top: 4px !important;
    }

    .tumblr_settings--tumblelog .website_theme_group .theme_button,
	.tumblr_settings .left_column .chrome {
        background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
        height: 40px !important;
        line-height: 38px !important;
        min-width: 130px !important;
        text-align: center !important;
    }

    .l-container.l-container--two-column .right_column {

        /*** SETTINGS CONTAINERS SIDEBAR ***/
        background-color: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        padding: 15px !important;
        margin-top: 0px;
    }

    .l-container.l-container--two-column #right_column * {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .identity .controls_section li.section_header {
     	border: none !important;   
    }

    .identity .controls_section li {
     	border-top: 1px dotted var(--border-color) !important;   
    }

    .l-container.l-container--two-column #right_column .controls_section.controls_section_settings .selected {
        background: transparent !important;
    }

    .l-container.l-container--two-column .controls_section.controls_section_settings {
        border-bottom: 1px dotted var(--border-color) !important;
        padding-bottom: 10px !important;
    }

    .identity .right_column::after {
        display: none !important;
    }

    .tumblelog_icon.tumblelog_icon--is_primary.icon_star_12 {
        margin-right: 10px !important;
    }

    /* FOLLOWING/FOLLOWERS */

    /** DESCRIPTION : change the elements of the following/followers page **/
    #tabs {

        /*** FOLLOWING/FOLLOWERS TITLE ***/
        background: transparent !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        width: 50% !important;
        position: absolute !important;
        top: 222px !important;
        left: 0px !important;
        right: 0px !important;
        margin: auto !important;
    }

    #dashboard_useraction_following #tabs .tab.selected {
        background: var(--card-bg) !important;
    	border-radius: 1px !important;    
        color: var(--primary-color) !important;
        border-bottom: 1px solid var(--border-color) !important;
    }

    .tab.selected,
    .title_and_controls h1 {
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .title_and_controls {
        margin-top: -10px !important;
        margin-bottom: -7px !important;
    }

    #dashboard_useraction_following #left_column,
    #followers-list #left_column {

        /*** FOLLOWING/FOLLOWERS CONTAINER ***/
        background: transparent !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        width: 50% !important;
        margin-bottom: 50px !important;
        position: absolute !important;
        top: 290px !important;
        right: 0px !important;
        left: 0px !important;
        margin: auto !important;
    }

    #followers-list #left_column {
        top: 222px !important;
    }

    #followers-list #left_column.followers .white_pane {
        border-radius: 1px !important;
    }

    #dashboard_useraction_following #left_column #invite_someone {
        display: none !important;
    }

    #dashboard_useraction_following #left_column .follower,
    #followers-list #left_column .follower {

        /*** FOLLOWING/FOLLOWERS LIST ***/
        background: var(--card-bg) !important;
        border-bottom: 1px solid var(--border-color) !important;
        padding: 15px !important;
    }

    .title_and_controls + .followers {
        border-top: 1px solid var(--border-color) !important;
        margin-left: -28px !important;
        margin-right: -28px !important;
    }

    #dashboard_useraction_following #left_column .follower .info,
    #followers-list #left_column .follower .info {
        width: 100% !important;
    }

    #dashboard_useraction_following #left_column .follower .info .name a,
    #followers-list #left_column .follower .info .name .name-link {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed;
    }

    #followers-list #left_column .ignore {
        border-radius: 1px !important;
        font-size: 11pt !important;
        font-stretch: condensed !important;
        padding: 5px 10px !important;
        text-transform: uppercase !important;
    }

    #dashboard_useraction_following #left_column .last_updated,
    #followers-list #left_column .blog-title {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    #dashboard_useraction_following .snowman-container .snowman-icon::before {
        color: transparent !important;
        background: no-repeat url("https://i.imgur.com/M3mkHmO.png") center !important;
        background-size: cover !important;
        width: 50px !important;
        height: 50px !important;
        padding: 2px !important;
        margin-left: -10px !important;
    }

    #dashboard_useraction_following #left_column .controls button {
        border: none !important;
        background: #FF6766 !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        letter-spacing: .1px !important;
        padding: 4px 15px !important;
        text-transform: uppercase !important;
    }

    #followers-list #left_column .follower .controls button {
        color: #FFF !important;
        font-size: 11pt !important;
        font-stretch: condensed !important;
        height: 35px !important;
        letter-spacing: .1px !important;
        line-height: 30px !important;
        text-transform: uppercase !important;
    }

    #dashboard_useraction_following #left_column #pagination,
    #followers-list #left_column #pagination {

        /*** FOLLOWING/FOLLOWERS PAGINATION ***/
        width: 100% !important;
        padding: 20px !important;
        position: absolute !important;
    }

    #dashboard_useraction_following #left_column .pagination .previous,
    #dashboard_useraction_following #left_column .pagination .next,
    #followers-list #left_column .pagination .previous,
    #followers-list #left_column .pagination .next {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        height: auto !important;
        letter-spacing: .1px !important;
        text-transform: uppercase !important;
    }

    #dashboard_useraction_following #right_column,
    #dashboard_useraction_followers #right_column {
        display: none !important;
    }

    /* INBOX */

    /** DESCRIPTION : change the elements of the inbox page **/
    #dashboard_messages_inbox #right_column {
        display: none !important;
    }

}

sections.code:
/* ==UserStyle==
@name           Thunder-W
@namespace      https://github.com/Catgrills/Thunder-W
@version        1.0.0
@description    An userstyle for Tumblr called Thunder-W.
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F0F1F5 !important; 
		--card-bg: #FFF !important;
		--card-bg-2: #F2F2F2 !important;
		--border-color: #EBEBEB !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #201A23 !important; 
		--card-bg: #022B3A !important;
		--card-bg-2: #40434E !important;
		--border-color: #080705 !important;
		--primary-color: #FFF !important;
		--secondary-color: #BEBEBE !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */

:root {
	    --body-bg: #201A23 !important; 
		--card-bg: #022B3A !important;
		--card-bg-2: #40434E !important;
		--border-color: #080705 !important;
		--primary-color: #FFF !important;
		--secondary-color: #BEBEBE !important;
		--invert: invert(1) !important;
	}

/* GENERAL */

    /** DESCRIPTION : change the elements of all page **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
    }

    body {
        overflow-x: hidden;
    }

	input::-moz-placeholder{
    	font-size: 14pt !important;
        font-stretch: condensed;
	}

    .popover_menu_item:hover,
    .blog-list-item:hover,
    .blog-sub-nav-details .blog-sub-nav-item:hover,
    .blog-sub-nav-details .blog-sub-nav-item.currentItem,
    .blog-sub-nav-details .blog-sub-nav-item.selected,
    .blog-sub-nav-details .blog-sub-nav-item:hover {
        background: transparent !important;
        box-shadow: none !important;
    }

    .postforms-open .l-container,
    .l-container.l-container--two-column-dashboard {

        /*** CONTAINER POSITION ***/
        position: absolute !important;
        width: 100% !important;
    }

    .post-forms-glass.active {

        /*** GLASS CONTAINER ***/
        display: none !important;
    }

    .ui_peepr_glass {
        background: rgba(52, 73, 94, .8) !important;
    }

    .results_end_body {

        /*** NO RESULT CONTAINER ***/
        color: #212121 !important;
        font-stretch: condensed !important;
    }

    .results_end_body .search_query_highlight {
        color: #83D6DE !important;
    }

    .no_posts_found {
        background: var(--body-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding: 10px !important;
        text-transform: lowercase !important;
    }

    .elevator-wrapper.visible {

        /*** TOP BUTTON ***/
        position: fixed !important;
        left: 40px !important;
        bottom: 45px !important;
    }

    #search_actions_search .elevator-wrapper.visible {
        bottom: 65px !important;
    }

    #discover_actions_discover .elevator-wrapper.visible {
        bottom: 45px !important;
        left: 28px !important;
    }

    .elevator-wrapper.visible .elevator {
        background-color: transparent !important;
        color: #009FB7 !important;
        font-size: 21pt !important;
    }

    .account-sessions.settings_group,
    .blogs_wrapper.indash_blogs,
    .nipple,
    .notification.single_notification.alt.takeover-container.big-margin,
    .popover_legal_link,
    #posts.posts > li.notification.single_notification,
    .post_brick .post_tags .post_tag::before,
	.post-brick-source-footer,
    .search_controls .right_controls,
    .tags-and-tumblelogs {

        /*** MISC ***/
        display: none !important;
    }

    .l-footer {
       visibility: hidden;   
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .l-header-container--refresh {
        padding: 10px !important;
    }

    #trigger-btn {
		background: no-repeat url("https://i.imgur.com/pGlESDe.png") center !important;
		background-color: transparent !important;
		background-size: 25px auto !important;
		border: none !important;
		color: transparent !important;
		height: 20px !important;
		width: 30px !important;
        position: fixed !important;
        top: 27px !important;
        left: 90px !important;
        filter: invert(1) !important;
    }

    .l-header-container .logo {
        margin-left: 20px !important;
        margin-right: 20px !important;
    }

    .l-header-container .logo .png-logo {
        background: no-repeat url("https://upload.wikimedia.org/wikipedia/commons/e/e0/Tumblr_icon.png") center !important;
        background-size: 40px auto !important;
        margin-top: 0px !important;
        margin-right: -10px !important;
    }

    .l-header-container .logo .png-logo:hover {
        opacity: .6 !important;
    }

    .preload-container {
        display: none !important;
    }

    .l-header-container #search_query {
        border-radius: 2px !important;
        font-family: "Roboto" !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        padding: 0px 11px 2px 11px !important;
    }

    .is-following.show-unfollow.search-follow-button .unfollow-text, 
    .worded-follow-button.is-following.show-unfollow .unfollow-text,
	.search-follow-button {
        display: none !important;
    }

    .l-header-container .ui_search.active .search_query,
    .l-header-container .ui_search.selected .search_query {
        background: rgba(0, 0, 0, .13) !important;
        color: inherit !important;
        transition: background 75ms, color 75ms;
    }

    .l-header-container .popover {
        background: var(--card-bg) !important;
        border-radius: 1px !important;
        border-top: 6px solid #1DABB8 !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 10px !important;
        margin-left: 3px !important;
    }

    .l-header-container .search_popover .search_results_section .search_subheading {
        display: none !important;
    }

    .l-header-container .popover .result_title {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        letter-spacing: .5px !important;
    }

    .l-header-container .result_sub_title {
        font-family: "Roboto" !important;
        font-size: 10pt !important;
