    /* SETTINGS */

    /** DESCRIPTION : change the elements of the setting page **/
    body[id*="settings_actions"] .l-container.l-container--two-column .l-content {

        /*** SETTINGS CONTAINERS ***/
        margin-top: 130px !important;
    }

    .l-container.l-container--two-column-dashboard .l-content,
    .l-container.l-container--two-column .l-content {

        /*** SETTINGS CONTAINERS BACKGROUND ***/
        background: var(--body-bg) !important;
    }

     .l-container.l-container--two-column .left_column {
		width: 600px !important;
    }

    .tumblr_settings .settings_heading {
        border-bottom: 1px solid var(--border-color) !important;
    }

    .tumblr_settings .settings_group + .settings_group,
    .tumblr_settings .settings_group + .settings_footer {
         border-top: 1px solid var(--border-color) !important;       
    }

    .l-container.l-container--two-column #left_column.white_pane {

        /*** SETTINGS CONTAINERS SETTINGS FORM ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        padding-bottom: 20px !important;
    }

    .indash_header_wrapper,
    .tumblr_settings--labs .settings-header-image {
        border-radius: 1px !important;
    }

    .settings_group_wrapper .settings_group {
        padding-top: 40px !important;
        padding-bottom: 30px !important;
    }

    .settings_subheading {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        letter-spacing: 0px !important;
    }

    .group_content * {
        color: var(--primary-color) !important;
        font-size: 12pt !important;
    }

    .l-container.l-container--two-column #left_column {

        /*** SETTINGS CONTAINERS MAIN BOX ***/
        padding: 0px !important;
    }

    .l-container.l-container--two-column #left_column .settings_heading {
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        line-height: 25px !important;
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .settings_subheading {
        padding: 0px 20px !important;
    }

    .l-container.l-container--two-column #left_column .apps_heading {
        display: none !important;
    }

    .l-container.l-container--two-column #left_column .settings_group .group_label {
        padding: 0 3px !important;
    }

    .l-container.l-container--two-column #left_column .app.app--promo-row {
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .help_text {
        font-size: 11pt !important;
        padding: 20px !important;
    }

    .l-container.l-container--two-column #left_column .tumblr_settings .help_text a {
        font-size: 11pt !important;
    }

    .accordion_header.clearfix, 
    .accordion_header.clearfix * {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .tumblr_settings .setting.checkbox .binary_switch {
        top: 4px !important;
    }

    .tumblr_settings--tumblelog .website_theme_group .theme_button,
	.tumblr_settings .left_column .chrome {
        background: var(--card-bg-2) !important;
		border: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
        height: 40px !important;
        line-height: 38px !important;
        min-width: 130px !important;
        text-align: center !important;
    }

    .l-container.l-container--two-column .right_column {

        /*** SETTINGS CONTAINERS SIDEBAR ***/
        background-color: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        padding: 15px !important;
        margin-top: 0px;
    }

    .l-container.l-container--two-column #right_column * {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .identity .controls_section li.section_header {
     	border: none !important;   
    }

    .identity .controls_section li {
     	border-top: 1px dotted var(--border-color) !important;   
    }

    .l-container.l-container--two-column #right_column .controls_section.controls_section_settings .selected {
        background: transparent !important;
    }

    .l-container.l-container--two-column .controls_section.controls_section_settings {
        border-bottom: 1px dotted var(--border-color) !important;
        padding-bottom: 10px !important;
    }

    .identity .right_column::after {
        display: none !important;
    }

    .tumblelog_icon.tumblelog_icon--is_primary.icon_star_12 {
        margin-right: 10px !important;
    }

    /* FOLLOWING/FOLLOWERS */

    /** DESCRIPTION : change the elements of the following/followers page **/
    #tabs {

        /*** FOLLOWING/FOLLOWERS TITLE ***/
        background: transparent !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        width: 50% !important;
        position: absolute !important;
        top: 222px !important;
        left: 0px !important;
        right: 0px !important;
        margin: auto !important;
    }

    #dashboard_useraction_following #tabs .tab.selected {
        background: var(--card-bg) !important;
    	border-radius: 1px !important;    
        color: var(--primary-color) !important;
        border-bottom: 1px solid var(--border-color) !important;
    }

    .tab.selected,
    .title_and_controls h1 {
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .title_and_controls {
        margin-top: -10px !important;
        margin-bottom: -7px !important;
    }

    #dashboard_useraction_following #left_column,
    #followers-list #left_column {

        /*** FOLLOWING/FOLLOWERS CONTAINER ***/
        background: transparent !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        width: 50% !important;
        margin-bottom: 50px !important;
        position: absolute !important;
        top: 290px !important;
        right: 0px !important;
        left: 0px !important;
        margin: auto !important;
    }

    #followers-list #left_column {
        top: 222px !important;
    }

    #followers-list #left_column.followers .white_pane {
        border-radius: 1px !important;
    }

    #dashboard_useraction_following #left_column #invite_someone {
        display: none !important;
    }

    #dashboard_useraction_following #left_column .follower,
    #followers-list #left_column .follower {

        /*** FOLLOWING/FOLLOWERS LIST ***/
        background: var(--card-bg) !important;
        border-bottom: 1px solid var(--border-color) !important;
        padding: 15px !important;
    }

    .title_and_controls + .followers {
        border-top: 1px solid var(--border-color) !important;
        margin-left: -28px !important;
        margin-right: -28px !important;
    }

    #dashboard_useraction_following #left_column .follower .info,
    #followers-list #left_column .follower .info {
        width: 100% !important;
    }

    #dashboard_useraction_following #left_column .follower .info .name a,
    #followers-list #left_column .follower .info .name .name-link {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed;
    }

    #followers-list #left_column .ignore {
        border-radius: 1px !important;
        font-size: 11pt !important;
        font-stretch: condensed !important;
        padding: 5px 10px !important;
        text-transform: uppercase !important;
    }

    #dashboard_useraction_following #left_column .last_updated,
    #followers-list #left_column .blog-title {
        color: var(--secondary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    #dashboard_useraction_following .snowman-container .snowman-icon::before {
        color: transparent !important;
        background: no-repeat url("https://i.imgur.com/M3mkHmO.png") center !important;
        background-size: cover !important;
        width: 50px !important;
        height: 50px !important;
        padding: 2px !important;
        margin-left: -10px !important;
    }

    #dashboard_useraction_following #left_column .controls button {
        border: none !important;
        background: #FF6766 !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        letter-spacing: .1px !important;
        padding: 4px 15px !important;
        text-transform: uppercase !important;
    }

    #followers-list #left_column .follower .controls button {
        color: #FFF !important;
        font-size: 11pt !important;
        font-stretch: condensed !important;
        height: 35px !important;
        letter-spacing: .1px !important;
        line-height: 30px !important;
        text-transform: uppercase !important;
    }

    #dashboard_useraction_following #left_column #pagination,
    #followers-list #left_column #pagination {

        /*** FOLLOWING/FOLLOWERS PAGINATION ***/
        width: 100% !important;
        padding: 20px !important;
        position: absolute !important;
    }

    #dashboard_useraction_following #left_column .pagination .previous,
    #dashboard_useraction_following #left_column .pagination .next,
    #followers-list #left_column .pagination .previous,
    #followers-list #left_column .pagination .next {
        font-size: 14pt !important;
        font-stretch: condensed !important;
